import { memo, useState } from "react";
import Text from "../../../components/pageTitle/text";
import LinkButton from "../../../components/button/LinkButton";
import Flex from "../../../components/Flex/Flex";
import usePost from "../../../hooks/usePost";

const BasicData = () => {
    const { setRow } = usePost({ link: "UPLOAD", noAuthorization: true });
    const [file, setFile] = useState<any>()
    console.log(d)
    const handleUploadClick = () => {
        if (!file) {
            return;
        }

        // ðŸ‘‡ Uploading the file using the fetch API to the server
        fetch('https://httpbin.org/post', {
            method: 'POST',
            body: file,
            // ðŸ‘‡ Set headers manually for single file upload
            headers: {
                'content-type': file.type,
                'content-length': `${file.size}`, // ðŸ‘ˆ Headers need to be a string
            },
        })
            .then((res) => res.json())
            .then((data) => console.log(data))
            .catch((err) => console.error(err));
    };
    return (
        <>
            <Text title="bscdat" margin="20px 10px" />
            <Flex justifyContent="space-around" width="40%" margin="30px" borderd>
                <LinkButton label="splrs" pathTo="suppliers" width="40%" />
                <LinkButton label="cstmrs" pathTo="customers" width="40%" />
            </Flex>
            <Flex justifyContent="space-around" width="40%" margin="30px">
                <LinkButton label="itms" pathTo="items" width="40%" />
                <LinkButton label="prntptn" pathTo="printOptions" width="40%" />
            </Flex>
            <div className="mb-3">
                <label htmlFor="formFileMultiple" className="form-label">Multiple files input example</label>
                <input className="form-control" type="file" id="formFileMultiple" multiple onChange={(event) => { x(event.target.files) }} />
            </div>
            <button type="submit" onClick={handleUploadClick}>upload</button>
        </>
    )
}

export default memo(BasicData)
