import { memo } from "react";
interface SelectProps {
    Option?: {
        label: string,
        value: number
    };
    Options: {
        label: string,
        value: number
    }[]
    setValue?: any;
    setLabel?: any;
    value?: number;
}

const Select = ({ Options, setValue, setLabel, value = 0 }: SelectProps) => {
    //@ts-ignore
    const handleValue = (event) => {
        console.log("dd")
        setLabel(event.target.options[event.target.selectedIndex].text)
        setValue(event.target.value)
        setLabel(event.target.options[event.target.selectedIndex].text)
    }
    return (
        <>
            <select name="cars" onChange={handleValue} placeholder="">
                <option value="" hidden ></option>
                {Options.map((Option) => {
                    return (
                        <option value={Option.value} selected={value === Option.value && true}> {Option.label}</option>
                    )
                })}
            </select>
        </>
    )
}

export default memo(Select)
