import React, { memo, useCallback } from 'react';
import Modal from "../../../components/Modal/Modal";
import InputText from '../../../components/InputText/InputText';
import useFormManager from '../../../hooks/useFormManager';

interface ModalViewProp {
    visible: boolean;
    onOK: () => void;
    onClose: () => void;
    selectedRow: any;
    setSelectedRow: any
}

const ModalView = ({
    visible,
    onOK,
    onClose,
    setSelectedRow,
    selectedRow
}: ModalViewProp) => {

    const onChangeName = useCallback((event?: any) => {
        setSelectedRow({ ...selectedRow, item_name: event.target.value })
    }, [selectedRow, setSelectedRow]);
    const onChangeEmail = useCallback((event?: any) => {
        setSelectedRow({ ...selectedRow, unit: event.target.value })
    }, [selectedRow, setSelectedRow])
    const onChangePhone = useCallback((event?: any) => {
        setSelectedRow({ ...selectedRow, base_price: event.target.value })
    }, [selectedRow, setSelectedRow])

    const { state, onChange } = useFormManager(selectedRow, setSelectedRow)
    //@ts-ignore
    const { item_name, unit, base_price } = state

    return (
        <Modal
            visible={visible}
            label={"Details"}
            onOK={onOK}
            onClose={onClose}
        >
            <InputText
                name="item_name"
                Label='Name'
                onChange={onChange}
                value={item_name}
            />
            <InputText
                name="unit"
                Label='Unit'
                onChange={onChange}
                value={unit}
            />
            <InputText
                name="base_price"
                Label='Base Price'
                onChange={onChange}
                value={base_price}
            />
        </Modal>
    )
};

export default memo(ModalView)
