import { memo } from "react";
import Flex from "../Flex/Flex";
import {
    Accordion,
    AccordionItem,
    AccordionButton,
    AccordionPanel,
    AccordionIcon,
    Box
} from '@chakra-ui/react';
import { useTranslation } from 'react-i18next';
import LinkButton from "../button/LinkButton";

const SideBar = () => {
    const { t } = useTranslation()
    return (
        <>
            <Flex backgroundColor="#3c8dcf" width="100%" margin="0 10px 0 0" justifyContent="center" height="100%" borderRadius="0">
                <Accordion width="100%">
                    <AccordionItem>
                        <h2>
                            <AccordionButton _expanded={{ bg: 'tomato', color: 'white' }}>
                                <Box as="span" flex='1' textAlign='left'>
                                    {t("bscdat")}
                                </Box>
                                <AccordionIcon />
                            </AccordionButton>
                        </h2>
                        <AccordionPanel>
                            <LinkButton label="splrs" pathTo="suppliers" width="100%" margin="" />
                            <LinkButton label="cstmrs" pathTo="customers" width="100%" />
                            <LinkButton label="itms" pathTo="items" width="100%" />
                            <LinkButton label="prntptn" pathTo="printOptions" width="100%" />
                        </AccordionPanel>
                    </AccordionItem>
                </Accordion>
            </Flex>
        </>
    )
}

export default memo(SideBar)
