import { memo, useEffect } from "react";
import Select from "./Select";
import useFetch from "../../hooks/useFetch";

interface SelectWithApiProps {
    Api: string;
    onChange?: any;
    Label?: boolean;
    value?: number
    name?: string;
    width?: number | string;
}

const SelectWithApi = ({ width, Api, onChange, Label, name = "", value = 0 }: SelectWithApiProps) => {

    const { data } = useFetch(Api)

    return (
        <>
            <Select
                Options={data}
                onChange={onChange}
                Label={Label}
                value={value}
                name={name}
                width={width}
            />
        </>
    )
}

export default memo(SelectWithApi);
