import { memo, useCallback, useEffect } from 'react';
import './Style.css';
import { Button } from "@components/button";
import InputText from "@components/input-text";
import useFormManager from '../../hooks/useFormManager';
import usePost from '../../hooks/usePost';
import useLoacalStorage from '../../hooks/useLocalStorage';

const SignInPage = () => {

    const { setRow, success } = usePost({ link: "USER_LOG_IN", noAuthorization: true })
    const { state, onChange } = useFormManager({})
    const { authorization, changeLocalStorage } = useLoacalStorage()

    if (success) {
        changeLocalStorage([
            {
                name: "salam",
                //@ts-ignore
                data: { authorization: success.authorization, display_name: success.display_name }
            }
        ])
    }

    useEffect(() => {
        if (authorization) {
            window.location.assign("/home")
        }
    }, [authorization])

    // @ts-ignore
    if (success && success.response === "success") {
        window.location.assign("/home")
    }

    const handleLogIn = useCallback(() => {
        setRow(state)

    }, [setRow, state])

    return (
        <>
            <div className="sign_in">
                <div className="card">
                    <div className="card-body">
                        <h1>مرحبا</h1>
                        <p>قم بتسجيل الدخول الى النظام</p>
                        <InputText
                            name='user_name'
                            Label='usrnm'
                            onChange={onChange}
                            width="90%"
                        />
                        <InputText
                            name='user_password'
                            Label='pswrd'
                            onChange={onChange}
                            type="password"
                            width="90%"
                        />
                        <Button
                            label='login'
                            onClick={handleLogIn}
                            width='40%'
                            margin='10px'
                        />
                    </div>
                </div>
            </div>
        </>
    )
};

export default memo(SignInPage);
