import { memo, useCallback, useEffect } from 'react';
import Modal from "../../../components/Modal/Modal";
import InputText from '../../../components/InputText/InputText';
import useFormManager from '../../../hooks/useFormManager';

interface ModalViewProp {
    visible: boolean;
    onOK: () => void;
    onClose: () => void;
    selectedRow: any;
    setSelectedRow: any
}

const ModalView = ({
    visible,
    onOK,
    onClose,
    setSelectedRow,
    selectedRow
}: ModalViewProp) => {

    const { state, onChange } = useFormManager(selectedRow)
    //@ts-ignore
    const { print_option, note } = state

    const handleSave = useCallback(() => {
        setSelectedRow(state)
        onOK()
    }, [onOK, setSelectedRow, state])

    console.log(state)



    return (
        <Modal
            visible={visible}
            label={"Details"}
            onOK={handleSave}
            onClose={onClose}
        >
            <InputText
                name='print_option'
                onChange={onChange}
                value={print_option}
                Label="Print Option"
            />
            <InputText
                name='notes'
                onChange={onChange}
                value={note}
                Label="Notes"
            />
        </Modal>
    )
};

export default memo(ModalView)
