import { memo, useCallback } from "react";
import { InputTextProps } from "./interface"
import "./Style.css"

const InputText = ({ disabled, value, name, Label, onChange }: InputTextProps) => {

    const handleChange = useCallback((event: { target: { value: string; }; }) => {
        onChange({ name: name, value: event.target.value })
    }, [name, onChange])

    return (
        <>

            {/* <div className="input">
                <input disabled={disabled} type="text" value={value} placeholder={Label} onChange={handleChange} />
            </div> */}
            <div className="mb-3">
                <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
                <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="name@example.com" />
            </div>

        </>
    )
}

export default memo(InputText)
